package com.example.videojuegos.dao

import com.example.videojuegos.interfaces.InterfaceDao
import com.example.videojuegos.models.Videojuego

class DaoVideojuegos : InterfaceDao {

    private val lista = mutableListOf(
        Videojuego(1, "Zelda", "Su trama central narra las heroicas misiones de Link, un joven guerrero, que debe rescatar a la Princesa Zelda y proteger el reino de Hyrule de las fuerzas oscuras, generalmente lideradas por el villano Ganondorf. Link utiliza la legendaria Espada Maestra y resuelve complejos acertijos en mazmorras, buscando la reliquia sagrada conocida como la Trifuerza.", "https://www.nintendo.com/eu/media/images/10_share_images/portals_3/2x1_Hub_TheLegendOfZelda_ToTK_image1600w.jpg"),
        Videojuego(2, "Mario", "Mario es el icónico fontanero italiano, mascota de Nintendo y protagonista de la serie de videojuegos más vendida de la historia.", "data:image/jpeg;base64,")
    )

    override fun getAll() = lista

    override fun delete(id: Int) {
        lista.removeIf { it.id == id }
    }

    override fun insert(videojuego: Videojuego) {
        lista.add(videojuego)
    }

    override fun update(videojuego: Videojuego) {
        val index = lista.indexOfFirst { it.id == videojuego.id }
        if (index != -1) lista[index] = videojuego
    }
}
